// Handler
struct t_scenario_data scenario;


// FUNCTIONS //////////////////////////////////////////////

//---------------------------------------------------------
// LOAD MAPS
//---------------------------------------------------------
void Scenario_load(u8 id)
{
	scenario.palette = scenarios[id].bgb->palette;

    scenario.atileset = scenarios[id].bga->tileset;
    scenario.btileset = scenarios[id].bgb->tileset;

    Scenario_loadTileset();
    Scenario_setPalette();
    Scenario_loadMaps(id);

}


//---------------------------------------------------------
// LOAD MAPS
//---------------------------------------------------------
void Scenario_loadMaps(u8 id)
{
	if (scenario.amap != NULL) MEM_free(scenario.amap);
	if (scenario.bmap != NULL) MEM_free(scenario.bmap);

	scenario.amap =  unpackTileMap(scenarios[id].bga->tilemap, NULL);
	scenario.bmap =  unpackTileMap(scenarios[id].bgb->tilemap, NULL);

	// WIP - TODO: do no copy moving elements/enemies
	MEM_free(scenario.hardnessmap);
	memcpy(scenario.hardnessmap, scenarios[id].hardnessmap, SCENARIO_NUM_TILES);
}


//---------------------------------------------------------
// SET REGION
//---------------------------------------------------------
void Scenario_setRegion(u16 scnx, u16 scny, u16 mapx, u16 mapy, u8 width, u8 height)
{
    VDP_setMapEx(BG_B, scenario.bmap,
    	TILE_ATTR_FULL(SCENARIO_DEFAULT_PAL, TRUE, FALSE, FALSE, SCENARIO_BPLAN_VRAM_POS),
		scnx, scny, mapx, mapy, width, height);

    VDP_setMapEx(BG_A, scenario.amap,
    	TILE_ATTR_FULL(SCENARIO_DEFAULT_PAL, TRUE, FALSE, FALSE, SCENARIO_APLAN_VRAM_POS),
		scnx, scny, mapx, mapy, width, height);
}


